        /*Lorenzo 28/09/2021
         * 
         * This method insert a record in tbLog table
         * 
         * PARAMETERS IN:
         * 
         * - string sMethod: the method name that is inserting in tbLog
         * - int nCodeI1: integer that identify the sender branch of the log --> Refer to LogSender enum
         * - int nCodeI2: integer that identify the log type --> Refer to LogType enum
         * - string sMessage: string that represent the description of the log record
         * 
         * PARAMETER OUT:
         * 
         * - enExitFunction: enum that could return kOk if the table exist, kKo if the table doesn't exist
         */
        public enExitFunction mLog(string sMethod, int nCodeI1, int nCodeI2, string sMessage)
        {
            try
            {
                string sSQL = @"INSERT INTO tbLog (sApplication,sMethod,nCodeI1,nCodeI2,sDescription)
                                        VALUES (@sApplication,@sMethod,@nCodeI1,@nCodeI2,@sDescription)";

                SQLiteCommand xLocCommand = new SQLiteCommand(sSQL, xDbLogConnection);

                xLocCommand.Parameters.AddWithValue("@sApplication", "T-Bot");
                xLocCommand.Parameters.AddWithValue("@sMethod", sMethod);
                xLocCommand.Parameters.AddWithValue("@nCodeI1", nCodeI1);
                xLocCommand.Parameters.AddWithValue("@nCodeI2", nCodeI2);
                xLocCommand.Parameters.AddWithValue("@sDescription", sMessage);

                xLocCommand.ExecuteNonQuery();

                return enExitFunction.kOk;
            }
            catch (Exception ex)
            {
                return enExitFunction.kKo;
            }
        }

