        private enExitFunction mCloseReader(ref SQLiteDataReader xLocReader)
        {
            try
            {
                if(xLocReader != null)
                {
                    if (xLocReader.IsClosed == false)
                        xLocReader.Close();
                }

                return enExitFunction.kOk;
            }
            catch(Exception ex)
            {
                return enExitFunction.kKo;
            }
        }
